import UIKit

class DetalleViewController: UIViewController
{
    @IBOutlet weak var EventImage: UIImageView!
    @IBOutlet weak var EventName: UILabel!
    @IBOutlet weak var EventCode: UILabel!
    @IBOutlet weak var EventUbication: UILabel!
    @IBOutlet weak var EventTickets: UILabel!
    @IBOutlet weak var QRImage: UIImageView!
    var NameEvent:String = ""
    var ImageEvent:String = ""
    var CodeEvent:String = ""
    var UbicationEvent:String = ""
    var TicketsEvent:String = ""
    var despegable:Bool = false
    
    override func viewDidLoad()
    {
        super.viewDidLoad()
        
        let image = generateQRCode(from: Guillermito)
        
        QRImage.image = image
        EventImage.image = UIImage(named: ImageEvent)
        EventName.text = NameEvent
        EventCode.text = "Este es tu codigo:" + " " + CodeEvent
        EventTickets.text = TicketsEvent + " " + " Tickest"
        EventUbication.text = UbicationEvent
        
        if(despegable)
        {
            alert()
        }
    }
    
    override func didReceiveMemoryWarning()
    {
        super.didReceiveMemoryWarning()
    }
    
    func alert()
    {
        let alert = UIAlertController(title: "Compra Realizada", message:
            "La compra se a realizado de manera correcta aqui puede ver los detalles de su comprar", preferredStyle: .alert)
        
        alert.addAction(UIAlertAction(title: "Aceptar", style:
            .default, handler: nil))
        
        present(alert, animated: true, completion: nil)
    }
    
    func generateQRCode(from string: String) -> UIImage?
    {
        let data = string.data(using: String.Encoding.ascii)
        
        if let filter = CIFilter(name: "CIQRCodeGenerator")
        {
            filter.setValue(data, forKey: "inputMessage")
            let transform = CGAffineTransform(scaleX: 20, y: 20)
            
            if let output = filter.outputImage?.transformed(by: transform)
            {
                return UIImage(ciImage: output)
            }
        }
        
        return nil
    }
    
    
